//http://localhost:3009/Employee

import {useEffect, useState} from "react";
import "./index.css"
import { Link } from "react-router-dom";
function EmpList(){
    const [data,setData]=useState()
    useEffect(()=>{
        fetch("http://localhost:3009/Employee")
        .then((res)=>{
             return res.json()
        })
        .then((resp)=>{
            console.log(resp)
            setData(resp)
        })
    },[])
 
    const deleteData = (id) => {
      
            fetch("http://localhost:3009/Employee/" + id, {
                method: "DELETE"
            })
    }
   
    return(
        <div className="container">
            <div className="card">
                <div className="card-title">
                    <h1>Employee Management System</h1>
                </div>
                <div className="card-body">
        <Link to="/form" className="btn btn-success">Add New(+)</Link>
        <table className="table table-bordred">
        <thead className="table-dark text-white">
            <tr>
                <th>ID</th>
                <th>Name</th>
                <th>City</th>
                <th>Mobile</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
            {data?.map((item)=>(

            <tr>
                <td>{item.id}</td>
                <td>{item.name}</td>
                <td>{item.city}</td>
                <td>{item.mobile}</td>
                <td ><a onClick={()=>deleteData(item.id)} className="btn btn-danger" >Delete</a></td>
            </tr>
              ))}
        </tbody>
       </table>
       </div>
       </div>
        </div>
    )
}
export default EmpList;
