import {useEffect, useState} from "react";
import { useParams } from "react-router-dom"
import "./details.css"

function EmpDetails(){
    const [data,setData]=useState(null)
    const {empid}=useParams()

    useEffect(()=>{
        fetch("http://localhost:3009/Employee/"+empid)
        .then((res)=>{
             return res.json()
        })
        .then((resp)=>{
            console.log(resp)
            setData(resp)
        })
    },[])

    return(
        <div className="container-fluid" >
            <div className="card" style={{width: "18rem"}}>
             {data &&
            <div className="card-body">
            <h5 className="card-title">Emp ID: {data.id} </h5>
            <h5 className="card-title">Emp Name: {data.name} </h5>
            <h5 className="card-title">Emp City: {data.city} </h5>
            <h5 className="card-title">Emp Mobile: {data.mobile} </h5>
            <a  className="card-link">Back</a>           
  </div>
}
</div>
        </div>
    )
}
export default EmpDetails;
